<?phpClass M_home extends CI_Model {    protected $contact = 'tb_contact';    function findsCity() {        $this->db->select('*');        $this->db->from('tb_city');        $query = $this->db->get();        return ($query->num_rows() > 0) ? $query->result() : null;    }    public function add_contact($data) {        $this->db->insert($this->contact, $data);        if ($this->db->affected_rows() > 0) {            return true;        } else {            return false;        }    }}?>